transforms.result:
div.top-theme {
    margin: auto;
    margin-top: 15rem;
    text-align: center;
    font-family: "Noto Serif Display";
    font-size: 15rem;
    font-weight: 400;
    line-height: 100%;
}
span.nbsp {
    content: " ";
}
@media screen and (max-width: 820px) {
    div.top-theme {
        width: min-content;
        text-align: left;
        margin-top: 10rem;
    }
}
div.top-theme.displayed {
    animation-name: displayTheme;
    animation-duration: 4s;
    animation-fill-mode: forwards;
    animation-timing-function: ease-in-out;
}
@keyframes displayTheme {
    from {
        transform: scale(0.7);
    }
    to {
        transform: scale(1);
    }
}
div.theme-desc {
    text-align: center;
    font-size: 1.6rem;
    margin-top: 2rem;
}
div.theme-desc span {
    font-size: 2.4rem;
}
div#to-works {
    cursor: pointer;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: auto;
    margin-top: 7rem;
    width: 12rem;
    height: 12rem;
    border-radius: 50%;
    border: 1px solid white;
    font-size: 2rem;
    transition: 0.5s;
    transform-origin: bottom;
}
div#to-works::before {
    content: "";
    position: absolute;
    width: 95%;
    height: 95%;
    top: 2.5%;
    left: 2.5%;
    border-radius: 100%;
    background-color: white;
    opacity: 0;
    transition: 0.5s;
    z-index: -1;
}
div#to-works:hover {
    color: black;
}
div#to-works:hover::before {
    opacity: 1;
}
div#to-works:hover div.circle {
    opacity: 0;
}
div.circle-pos {
    position: absolute;
    bottom: 50%;
    width: 4rem;
    height: calc(50% + 2.5rem);
    transform: rotate(90deg);
    transform-origin: bottom;
    animation-name: circle-rotation;
    animation-duration: 5s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
}
div.circle-pos.small {
    width: 2.5rem;
    height: calc(50% + 1.5rem);
    transform: rotate(45deg);
    animation-duration: 3s;
}
@keyframes circle-rotation {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}
div.circle {
    width: 4rem;
    height: 4rem;
    border-radius: 50%;
    border: 1px solid white;
    animation-name: circle-size;
    animation-duration: 5s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
    transition: 0.5s;
}
div.circle.small {
    width: 2.5rem;
    height: 2.5rem;
}
@keyframes circle-size {
    0% {
        transform: scale(0.7);
    }
    50% {
        transform: scale(1.2);
    }
    100% {
        transform: scale(0.7);
    }
}
div.top-content {
    margin-top: 10rem;
}
div.content-title {
    font-size: 4rem;
    text-align: center;
}
div.content-txt {
    margin-top: 2rem;
    font-size: 1.6rem;
    text-align: center;
}
@media screen and (max-width: 500px) {
    br.sp {
        display: none;
    }
}
div.works-arr {
    display: flex;
    justify-content: center;
    gap: 2rem;
    margin-top: 2rem;
}
@media screen and (max-width: 500px) {
    div.works-arr {
        flex-wrap: wrap;
    }
}
div.work-image-container {
    display: grid;
    place-items: center;
    width: 50rem;
    min-width: 30rem;
    aspect-ratio: 5 / 3;
    background-color: #393939;
    border-radius: 2rem;
}
div.work-image-container img {
    max-width: 100%;
    width: 90%;
}
div.view-detail {
    position: relative;
    margin: auto;
    margin-top: 5rem;
    width: 10rem;
    height: 1rem;
    text-align: center;
    font-size: 1.6rem;
    line-height: 1.6rem;
    cursor: pointer;
}
div.view-detail::before {
    content: "";
    position: absolute;
    left: 0;
    top: 100%;
    width: 100%;
    height: 100%;
    border-right: 2.5px solid white;
    border-bottom: 1px solid white;
    z-index: -1;
    transform: skew(60deg);
}
div.skills-container {
    display: flex;
    margin-top: 2rem;
}
@media screen and (max-width: 500px) {
    div.skills-container {
        display: block;
    }
}
dl.skill-content {
    flex: 1;
    padding: 2rem;
    text-align: center;
    border: 0.5px solid #777;
}
dl.skill-content:not(:nth-of-type(1)) {
    margin-left: -0.5px;
}
dt.skill {
    font-size: 3rem;
    border-bottom: 0.5px solid #777;
}
dd.skill-detail {
    font-size: 2rem;
    margin-left: 0;
}
dd.skill-detail:nth-of-type(1) {
    margin-top: 2rem;
}
